import React, { useState, useEffect } from "react";
import ReactImageLightbox from "react-image-lightbox";
import "react-image-lightbox/style.css"; // Import Lightbox styles
import "./Gallery.css";

export default function Gallery() {
  const images = [
    "./A2rtboard.png",
    "./Artb12oard 1.png"
  ];

  const captions = [
    "Beautiful Abstract Design",  
    "Modern Digital Art"
  ];

  const [currentImage, setCurrentImage] = useState(0);
  const [isLightboxOpen, setIsLightboxOpen] = useState(false);
  const [isHoldActive, setIsHoldActive] = useState(false); // Hold button state

  // Function to open lightbox
  const openLightbox = () => setIsLightboxOpen(true);

  // Function to close lightbox
  const closeLightbox = () => setIsLightboxOpen(false);

  // Auto-slide effect every 4 seconds
  useEffect(() => {
    if (!isHoldActive) {
      const interval = setInterval(() => {
        setCurrentImage((prev) => (prev + 1) % images.length);
      }, 4000);

      return () => clearInterval(interval);
    }
  }, [images, isHoldActive]);

  return (
    <div className="gallery-container">
      <h1 className="gallery-title">Gallery</h1>

      <div className="gallery-image-container" onClick={openLightbox}>
        <img
          src={images[currentImage]}
          alt={`Image ${currentImage + 1}`}
          className="gallery-image"
        />
      </div>

      {/* Caption Box Below Image */}
      <div className="gallery-caption-box">
        <p className="gallery-caption">{captions[currentImage]}</p>
      </div>

      {/* Hold Button */}
      <button
        className="hold-button"
        onMouseDown={() => setIsHoldActive(true)}
        onMouseUp={() => setIsHoldActive(false)}
        onTouchStart={() => setIsHoldActive(true)}
        onTouchEnd={() => setIsHoldActive(false)}
      >
        Hold to Pause
      </button>

      {isLightboxOpen && (
        <ReactImageLightbox
          mainSrc={images[currentImage]}
          nextSrc={images[(currentImage + 1) % images.length]}
          prevSrc={images[(currentImage - 1 + images.length) % images.length]}
          onCloseRequest={closeLightbox}
          onMovePrevRequest={() =>
            setCurrentImage((currentImage - 1 + images.length) % images.length)
          }
          onMoveNextRequest={() =>
            setCurrentImage((currentImage + 1) % images.length)
          }
          imageCaption={captions[currentImage]} // Lightbox Caption
        />
      )}
    </div>
  );
}
