import React, { useEffect, useRef, useState } from "react";
import ReactImageLightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";
import "./Panel.css";
import "../Terminal/Terminal.css";

const InfoPanel = () => {
  const roles = [
    "Open Source Contributor",
    "MERN Developer",
    "Cybersecurity Enthusiast",
    "Social Worker",
    "Tech Blogger",
    "AI/ML Enthusiast",
  ];

  const sectionRefs = {
    home: useRef(null),
    gallery: useRef(null),
    projects: useRef(null),
    resume: useRef(null),
    contact: useRef(null),
  };

  const scrollToSection = (section) => {
    sectionRefs[section].current.scrollIntoView({ behavior: "smooth" });
  };

  // Contact area
  const [formData, setFormData] = useState({
    email: "",
    message: "",
  });

  const [responseMessage, setResponseMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.ok) {
        setResponseMessage("Message sent successfully!");
        setFormData({ email: "", message: "" });
      } else {
        setResponseMessage(data.error || "Something went wrong. Please try again.");
      }
    } catch (error) {
      setResponseMessage("Failed to send the message. Please check your connection.");
    }
  };

  const [currentRole, setCurrentRole] = useState("");
  const [roleIndex, setRoleIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  const images = [
    "https://imgs.search.brave.com/bT9XBpvB4E7X2hwgEoBO67lmULlMM6Z4z65MO17Lx7c/rs:fit:860:0:0:0/g:ce/aHR0cHM6Ly9tZWRp/YTEucG9wc3VnYXIt/YXNzZXRzLmNvbS9m/aWxlcy90aHVtYm9y/Lzl3MTdHVzRwR0tr/ajJGZmdjbGVpMEFR/V05EOD0vZml0LWlu/Lzc5Mng1MjgvdG9w/L2ZpbHRlcnM6Zm9y/bWF0X2F1dG8oKTp1/cHNjYWxlKCkvMjAy/MC8wNi8yNS8wMzAv/bi8xOTIyMjgzL2Rj/OTEwZTc2OGUzNmE0/MWVfREFSS18xMDVf/VW5pdF8wMjI4NVIu/anBn",
    "https://imgs.search.brave.com/4fizkDMUrNnJsYCuiW00XZqt1Fi9AhKAVlvYV8Mn8pY/rs:fit:500:0:0:0/g:ce/aHR0cHM6Ly9lMC5w/eGZ1ZWwuY29tL3dh/bGxwYXBlcnMvODkz/LzQ3My9kZXNrdG9w/LXdhbGxwYXBlci1k/YXJrLW1hcnRoYS1u/aWVsc2VuLXRodW1i/bmFpbC5qcGc",
    "https://imgs.search.brave.com/vht5PK-IgpL8ygqQf02LeN1HgP2DTAByzR1O_YnDcZM/rs:fit:500:0:0:0/g:ce/aHR0cHM6Ly9lMS5w/eGZ1ZWwuY29tL2Rl/c2t0b3Atd2FsbHBh/cGVyLzY3NS83MTcv/ZGVza3RvcC13YWxs/cGFwZXItZGFyay10/di1zaG93LWNoYXJh/Y3Rlci1saXN0LWRh/cmstbmV0ZmxpeC10/aHVtYm5haWwuanBn",
  ];

  const [currentImage, setCurrentImage] = useState(0);
  const [isLightboxOpen, setIsLightboxOpen] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    const typingSpeed = isDeleting ? 100 : 150;
    const pauseTime = 2000;

    const handleTyping = () => {
      const role = roles[roleIndex];

      if (isDeleting) {
        setCurrentRole(role.substring(0, charIndex - 1));
        setCharIndex(charIndex - 1);
      } else {
        setCurrentRole(role.substring(0, charIndex + 1));
        setCharIndex(charIndex + 1);
      }

      if (!isDeleting && charIndex === role.length) {
        setTimeout(() => setIsDeleting(true), pauseTime);
      } else if (isDeleting && charIndex === 0) {
        setIsDeleting(false);
        setRoleIndex((roleIndex + 1) % roles.length);
      }
    };

    const typingInterval = setInterval(handleTyping, typingSpeed);

    return () => clearInterval(typingInterval);
  }, [charIndex, isDeleting, roles, roleIndex]);

  return (
    <div className="info-panel">
      <canvas id="matrixCanvas"></canvas>
      <div className="panel-box scrollbar" id="scrollbar1">
        <div className="content" ref={sectionRefs.home}>
          <h1>
            Hello There! <span className="wave">👋</span>
          </h1>
          <h2>I'M VAIBHAV PATIL</h2>
          <p>{currentRole}</p>
        </div>

        <div className="gallery-box" ref={sectionRefs.gallery}>
          <h1 className="gallery-title">Auto-Changing Gallery</h1>
          <div className="gallery-image-container" onClick={() => setIsLightboxOpen(true)}>
            <img
              src={images[currentImage]}
              alt={`Image ${currentImage + 1}`}
              className="gallery-image"
            />
          </div>
          {isLightboxOpen && (
            <ReactImageLightbox
              mainSrc={images[currentImage]}
              nextSrc={images[(currentImage + 1) % images.length]}
              prevSrc={images[(currentImage - 1 + images.length) % images.length]}
              onCloseRequest={() => setIsLightboxOpen(false)}
            />
          )}
        </div>

        <div className="project-box" ref={sectionRefs.projects}>
          <h2 className="project-title">My Recent Projects</h2>
          <div className="row project-grid">
            <div className="col-md-6 project-item">Project 1</div>
            <div className="col-md-6 project-item">Project 2</div>
            <div className="col-md-6 project-item">Project 3</div>
            <div className="col-md-6 project-item">Project 4</div>
          </div>
        </div>

        <div className="resume-box" ref={sectionRefs.resume}>
          <h2>My Resume</h2>
          <br />
          <div>
            <img src="./image.png" style={{ width: "80%", height: "60%" }} alt="Resume" />
          </div>
        </div>

        <div className="contact-box" ref={sectionRefs.contact}>
          <h2>Contact</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div>
              <label htmlFor="message">Message:</label>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit">Send</button>
          </form>
          {responseMessage && <p>{responseMessage}</p>}
        </div>
      </div>
    </div>
  );
};

export default InfoPanel;
