import React, { useEffect, useState } from "react";
import "./Panel.css";

const InfoPanel = () => {
  const roles = [
    "Open Source Contributor",
    "MERN Developer",
    "Cybersecurity Enthusiast",
    "Social Worker",
    "Tech Blogger",
  ];

  const [currentRole, setCurrentRole] = useState("");
  const [roleIndex, setRoleIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    const typingSpeed = isDeleting ? 100 : 150;
    const pauseTime = 2000;

    const handleTyping = () => {
      const role = roles[roleIndex];

      if (isDeleting) {
        setCurrentRole(role.substring(0, charIndex - 1));
        setCharIndex(charIndex - 1);
      } else {
        setCurrentRole(role.substring(0, charIndex + 1));
        setCharIndex(charIndex + 1);
      }

      if (!isDeleting && charIndex === role.length) {
        setTimeout(() => setIsDeleting(true), pauseTime);
      } else if (isDeleting && charIndex === 0) {
        setIsDeleting(false);
        setRoleIndex((roleIndex + 1) % roles.length);
      }
    };

    const typingInterval = setInterval(handleTyping, typingSpeed);

    return () => clearInterval(typingInterval);
  }, [charIndex, isDeleting, roles, roleIndex]);

  useEffect(() => {
    const canvas = document.getElementById("matrixCanvas");
    const ctx = canvas.getContext("2d");

    const resizeCanvas = () => {
      canvas.width = document.body.clientWidth;
      canvas.height = window.innerHeight;
    };

    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);

    const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
    const fontSize = 16;
    const columns = Math.floor(canvas.width / fontSize);
    const drops = Array(columns).fill(1);

    const drawMatrix = () => {
      ctx.fillStyle = "rgba(0, 0, 0, 0.05)";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = "#0f0";
      ctx.font = `${fontSize}px monospace`;

      drops.forEach((y, x) => {
        const text = letters[Math.floor(Math.random() * letters.length)];
        ctx.fillText(text, x * fontSize, y * fontSize);

        if (y * fontSize > canvas.height && Math.random() > 0.975) {
          drops[x] = 0;
        }
        drops[x]++;
      });
    };

    const interval = setInterval(drawMatrix, 50);

    return () => {
      clearInterval(interval);
      window.removeEventListener("resize", resizeCanvas);
    };
  }, []);

  return (
    <div className="info-panel">
      <canvas id="matrixCanvas"></canvas>
      <div className="content">
        <h1>
          Hello There! <span className="wave">👋</span>
        </h1>
        <h2>I'M VAIBHAV PATIL</h2>
        <p>{currentRole}</p>
      </div>
      <div className="gallery-container">
        <h2 className="gallery-title">My Gallery</h2>
        <div className="gallery">
          <img src="https://via.placeholder.com/300" alt="Gallery Item 1" />
          <img src="https://via.placeholder.com/300" alt="Gallery Item 2" />
          <img src="https://via.placeholder.com/300" alt="Gallery Item 3" />
          <img src="https://via.placeholder.com/300" alt="Gallery Item 4" />
          <img src="https://via.placeholder.com/300" alt="Gallery Item 5" />
          <img src="https://via.placeholder.com/300" alt="Gallery Item 6" />
        </div>
      </div>
    </div>
  );
};

export default InfoPanel;
