import React, { useState } from "react";
import ReactImageLightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";

export default function Gallery() {
  const imageSrc = "/Artb12oard 1.png"; // Path to the image in the public folder
  const images = Array(6).fill(imageSrc); // Creates an array with the same image multiple times

  const [isLightboxOpen, setIsLightboxOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState(0);

  const openLightbox = (index) => {
    setCurrentImage(index);
    setIsLightboxOpen(true);
  };

  return (
    <div className="gallery-container">
      <h1 className="gallery-title">My Gallery</h1>
      <div className="gallery-grid">
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Image ${index + 1}`}
            className="gallery-image"
            onClick={() => openLightbox(index)}
          />
        ))}
      </div>

      {isLightboxOpen && (
        <ReactImageLightbox
          mainSrc={images[currentImage]}
          nextSrc={images[(currentImage + 1) % images.length]}
          prevSrc={images[(currentImage - 1 + images.length) % images.length]}
          onCloseRequest={() => setIsLightboxOpen(false)}
          onMovePrevRequest={() =>
            setCurrentImage((currentImage - 1 + images.length) % images.length)
          }
          onMoveNextRequest={() =>
            setCurrentImage((currentImage + 1) % images.length)
          }
        />
      )}
    </div>
  );
}
