import React, { useState } from "react";

const Terminal = () => {
  const [input, setInput] = useState(""); // Current user input
  const [output, setOutput] = useState([]); // Command history and responses
  const username = "vaibhav"; // Mock username
  const hostname = "workbench"; // Mock hostname

  // Mock command handler
  const handleCommand = (command) => {
    switch (command.trim().toLowerCase()) {
      case "help":
        return "Available commands:\nhelp - Show available commands\nclear - Clear the terminal\nabout - About this terminal";

      case "clear":
        setOutput([]);
        return "";

      case "about":
        return "Welcome to Vaibhav's Terminal. This is a simulated terminal designed to showcase personal information and features.";

      case "":
        return ""; // Empty input does nothing

      default:
        return `Command not found: ${command}`;
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmedInput = input.trim();
    if (trimmedInput) {
      const response = handleCommand(trimmedInput);
      setOutput([...output, `$ ${trimmedInput}`, response]);
    }
    setInput(""); // Clear input field
  };

  return (
    <div
      className="terminal-container d-flex flex-column justify-content-start"
      style={{
        height: "100%",
        overflowY: "auto",
        padding: "1rem",
      }}
    >
      <div
        className="terminal-output"
        style={{
          flexGrow: 1,
          overflowY: "auto",
          whiteSpace: "pre-wrap",
        }}
      >
        {output.map((line, index) => (
          <div key={index} style={{ margin: "0.2rem 0" }}>
            {line}
          </div>
        ))}
      </div>

      <form
        className="terminal-input d-flex"
        style={{ marginTop: "1rem" }}
        onSubmit={handleSubmit}
      >
        <span
          style={{
            color: "lime",
            marginRight: "0.5rem",
            whiteSpace: "nowrap",
          }}
        >
          {username}@{hostname}:~$
        </span>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={{
            flex: 1,
            backgroundColor: "black",
            color: "lime",
            border: "none",
            outline: "none",
            fontFamily: "'Courier New', Courier, monospace",
          }}
        />
      </form>
    </div>
  );
};

export default Terminal;
