import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Panel.css";

const InfoPanel = () => {
  const roles = [
    "Open Source Contributor",
    "MERN Developer",
    "Cybersecurity Enthusiast",
    "Social Worker",
    "Tech Blogger",
    "AI/ML Enthusiast",
  ];

  const galleryItems = [
    { type: "image", src: "https://via.placeholder.com/300", alt: "Image 1" },
    { type: "image", src: "https://via.placeholder.com/300", alt: "Image 2" },
    { type: "video", src: "https://www.w3schools.com/html/mov_bbb.mp4", alt: "Video 1" },
    { type: "image", src: "https://via.placeholder.com/300", alt: "Image 3" },
    { type: "image", src: "https://via.placeholder.com/300", alt: "Image 4" },
    { type: "video", src: "https://www.w3schools.com/html/movie.mp4", alt: "Video 2" },
  ];

  const [currentRole, setCurrentRole] = useState("");
  const [roleIndex, setRoleIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    const typingSpeed = isDeleting ? 100 : 150;
    const pauseTime = 2000;

    const handleTyping = () => {
      const role = roles[roleIndex];

      if (isDeleting) {
        setCurrentRole(role.substring(0, charIndex - 1));
        setCharIndex(charIndex - 1);
      } else {
        setCurrentRole(role.substring(0, charIndex + 1));
        setCharIndex(charIndex + 1);
      }

      if (!isDeleting && charIndex === role.length) {
        setTimeout(() => setIsDeleting(true), pauseTime);
      } else if (isDeleting && charIndex === 0) {
        setIsDeleting(false);
        setRoleIndex((roleIndex + 1) % roles.length);
      }
    };

    const typingInterval = setInterval(handleTyping, typingSpeed);

    return () => clearInterval(typingInterval);
  }, [charIndex, isDeleting, roles, roleIndex]);

  return (
    <div className="info-panel">
      <canvas id="matrixCanvas"></canvas>
      <div className="panel-box">
        <div className="content">
          <h1>
            Hello There! <span className="wave">ðŸ‘‹</span>
          </h1>
          <h2>I'M VAIBHAV PATIL</h2>
          <p>{currentRole}</p>
        </div>
        <div className="gallery-box container mt-4">
          <div className="row g-4">
            {galleryItems.map((item, index) => (
              <div key={index} className="col-md-4 col-sm-6">
                {item.type === "image" ? (
                  <img
                    src={item.src}
                    alt={item.alt}
                    className="img-fluid rounded shadow-sm"
                  />
                ) : (
                  <video
                    src={item.src}
                    className="img-fluid rounded shadow-sm"
                    controls
                  />
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default InfoPanel;
