import React, { useState, useRef, useEffect } from "react";

const Terminal = () => {
  const [history, setHistory] = useState([]); // Command history
  const terminalRef = useRef(null); // Terminal container reference
  const inputRefs = useRef([]); // References for all input fields

  const username = "vaibhav";
  const hostname = "workbench";

  // Mock command handler
  const handleCommand = (command) => {
    switch (command.trim().toLowerCase()) {
      case "help":
        return "Available commands:\nhelp - Show available commands\nclear - Clear the terminal\nabout - About this terminal";

      case "clear":
        setHistory([]);
        return "";

      case "about":
        return "Welcome to Vaibhav's Terminal. This is a simulated terminal designed to showcase personal information and features.";

      case "":
        return ""; // Empty input does nothing

      default:
        return `Command not found: ${command}`;
    }
  };

  // Handle input changes for a specific command prompt
  const handleInputChange = (index, value) => {
    setHistory((prev) =>
      prev.map((entry, i) =>
        i === index ? { ...entry, input: value } : entry
      )
    );
  };

  // Handle form submission for a specific command prompt
  const handleSubmit = (e, index) => {
    e.preventDefault();
    const trimmedInput = history[index]?.input.trim();
    if (trimmedInput) {
      const response = handleCommand(trimmedInput);
      setHistory((prev) => [
        ...prev.slice(0, index),
        {
          ...prev[index],
          output: response,
          completed: true, // Mark the command as completed
        },
        { input: "", completed: false }, // Add a new empty prompt
      ]);
    }
  };

  // Auto-scroll to the bottom of the terminal output
  useEffect(() => {
    terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
  }, [history]);

  // Focus the last input field when the terminal updates
  useEffect(() => {
    if (inputRefs.current.length > 0) {
      inputRefs.current[inputRefs.current.length - 1]?.focus();
    }
  }, [history]);

  // Initialize the terminal with one prompt
  useEffect(() => {
    setHistory([{ input: "", completed: false }]);
  }, []);

  // Handle terminal click to focus on the last input
  const handleTerminalClick = () => {
    if (inputRefs.current.length > 0) {
      inputRefs.current[inputRefs.current.length - 1]?.focus();
    }
  };

  return (
    <div
      className="terminal-container"
      style={{
        height: "100%",
        backgroundColor: "black",
        color: "lime",
        fontFamily: "'Courier New', Courier, monospace",
        overflowY: "auto",
        padding: "1rem",
        cursor: "text",
      }}
      ref={terminalRef}
      onClick={handleTerminalClick} // Add click handler
    >
      {history.map((entry, index) => (
        <div key={index} style={{ marginBottom: "1rem" }}>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              whiteSpace: "nowrap",
            }}
          >
            <span
              style={{
                color: "lime",
                marginRight: "0.5rem",
              }}
            >
              {username}@{hostname}:~$
            </span>
            {entry.completed ? (
              <span>{entry.input}</span>
            ) : (
              <form
                onSubmit={(e) => handleSubmit(e, index)}
                style={{ flexGrow: 1 }}
              >
                <input
                  type="text"
                  value={entry.input}
                  onChange={(e) => handleInputChange(index, e.target.value)}
                  ref={(el) => (inputRefs.current[index] = el)} // Set input reference
                  style={{
                    width: "100%",
                    backgroundColor: "black",
                    color: "lime",
                    border: "none",
                    outline: "none",
                    fontFamily: "'Courier New', Courier, monospace",
                  }}
                  autoFocus={index === history.length - 1}
                />
              </form>
            )}
          </div>
          {entry.output && (
            <div style={{ marginTop: "0.5rem", whiteSpace: "pre-wrap" }}>
              {entry.output}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Terminal;
