import React, { useState, useRef, useEffect } from "react";

const Terminal = () => {
  const [history, setHistory] = useState([]); // Command history
  const [input, setInput] = useState(""); // Current user input
  const terminalRef = useRef(null); // Terminal container reference
  const inputRef = useRef(null); // Input field reference

  const username = "vaibhav";
  const hostname = "workbench";

  // Mock command handler
  const handleCommand = (command) => {
    switch (command.trim().toLowerCase()) {
      case "help":
        return (
          "Available commands:\n" +
          "help - Show available commands\n" +
          "clear - Clear the terminal\n" +
          "about - About this terminal"
        );

      case "clear":
        setHistory([]);
        return "";

      case "about":
        return "Welcome to Vaibhav's Terminal. This is a simulated terminal designed to showcase personal information and features.";

      case "":
        return ""; // Empty input does nothing

      default:
        return `Command not found: ${command}`;
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmedInput = input.trim();
    if (trimmedInput) {
      const response = handleCommand(trimmedInput);
      setHistory((prev) => [
        ...prev,
        `${username}@${hostname}:~$ ${trimmedInput}`,
        response,
      ]);
    } else {
      setHistory((prev) => [...prev, `${username}@${hostname}:~$`]);
    }
    setInput(""); // Clear input field
  };

  // Auto-scroll to the bottom of the terminal output
  useEffect(() => {
    terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
  }, [history]);

  // Focus the input when the terminal container is clicked
  const handleTerminalClick = () => {
    inputRef.current.focus();
  };

  return (
    <div
      className="terminal-container"
      style={{
        height: "100%",
        backgroundColor: "black",
        color: "lime",
        fontFamily: "'Courier New', Courier, monospace",
        overflowY: "auto",
        cursor: "text",
      }}
      ref={terminalRef}
      onClick={handleTerminalClick} // Trigger input focus on click
    >
      {/* Command History */}
      <div style={{ whiteSpace: "pre-wrap", paddingLeft: "1%" }}>
        {history.map((line, index) => (
          <div
            key={index}
            style={{
              marginLeft: line.startsWith(`${username}@${hostname}`)
                ? "0%"
                : "1%",
            }}
          >
            {line}
          </div>
        ))}
      </div>

      {/* Command Input */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          paddingLeft: "1%",
        }}
      >
        <span
          style={{
            color: "lime",
            marginRight: "0.5rem",
            whiteSpace: "nowrap",
          }}
        >
          {username}@{hostname}:~$
        </span>
        <form onSubmit={handleSubmit} style={{ flexGrow: 1 }}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            autoFocus
            ref={inputRef} // Reference for focusing
            style={{
              width: "100%",
              backgroundColor: "black",
              color: "lime",
              border: "none",
              outline: "none",
              fontFamily: "'Courier New', Courier, monospace",
            }}
          />
        </form>
      </div>
    </div>
  );
};

export default Terminal;
